CREATE TABLE `characters` (
  `id` int NOT NULL,
  `virtue_id` int DEFAULT NULL,
  `vice_id` int DEFAULT NULL,
  `attributes_id` int DEFAULT NULL,
  `skills_id` int DEFAULT NULL,
  `player_id` int DEFAULT NULL,
  `chronicle_id` int DEFAULT NULL,
  `name` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `age` int UNSIGNED DEFAULT NULL,
  `concept` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `faction` varchar(25) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `group_name` varchar(25) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `willpower` smallint NOT NULL,
  `moral` smallint NOT NULL,
  `wounds` json DEFAULT NULL,
  `size` smallint NOT NULL,
  `current_willpower` smallint NOT NULL,
  `xp_total` smallint NOT NULL,
  `xp_used` smallint NOT NULL,
  `type` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `is_npc` tinyint(1) NOT NULL,
  `virtue_detail` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `vice_detail` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `background` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `experience_logs` json NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

