INSERT INTO `character_note` (`id`, `character_id`, `content`, `assigned_at`, `author_id`, `title`, `type`) VALUES
(1, 1, '**2 sept**\n\nJeudi\n\n- Depouillage de téléphone\n \nVendredi\n\n- Plan de la fausse visite de musée\n- 4 bikers en attente\n \nSamedi\n\n- NOMA, exposition sur Sumeria, Sarah avec ses 2 mecs de la nuit, intérêt surprenant pour l\'exposition, sur le côté \"mythologique\" partagé avec Sarah.\n- Détail au troisième passage, dans un dessin (symbol), impression d\'un concept \"l\'éternelle reine des dieux\" Eneshtasha. Pour Sarah, double concept seigneur déesse au coeur de 3/4 de glace ou triple seigneur/déesse au coeur de glace\n- Sarah, for concept \"reine désir/besoin\" ... \"La grande déesse désirable\" Ninashtagal (Rochel Wright)\n- Directeur NOMA, vu au vernissage galerie avec Yenela.\n- Bruit de porte qui claque surprenant, me rappelle trébuchage d\'Aiden, puis coup de feu\n \nDimanche\n\n- Coup de feu 15-30min avant la nuit dans la maison\n \n\"Je suis celle dont tu as envie\"\n\nSarah est Guénifer\n\n\\-------------', '2005-06-28 00:00:00', 1, NULL, 1),
